import { Component } from '@angular/core';

@Component({
  selector: 'app-direct-message',
  templateUrl: './direct-message.component.html',
  styleUrls: ['./direct-message.component.css']
})
export class DirectMessageComponent {

}
<div class="h-screen p-4">

    <div class="messages">
        <p>Message</p>
    </div>


    <form class="flex gap-2">   
        <input type="message" id="search" class="block w-full shadow-lg p-4 pl-10 text-sm text-primary rounded-lg bg-darkSecondary" placeholder="Message" required>
        <button type="submit" class="text-white hover:bg-blue-800 focus:outline-none font-medium rounded-lg text-sm px-4 py-2">Send</button>
    </form>

</div>
