import { Component } from '@angular/core';

@Component({
  selector: 'app-direct-message',
  templateUrl: './direct-message.component.html',
  styleUrls: ['./direct-message.component.css']
})
export class DirectMessageComponent {

}
<div class="h-screen p-4">

    <form>
        <div class="mb-4">
            <label for="input" class="block text-sm font-medium text-gray-600">Input Field</label>
            <input type="text" id="input" name="input" class="w-full px-3 py-2 text-gray-700 border rounded-lg focus:outline-none focus:ring focus:border-blue-300" placeholder="Enter text">
        </div>
        <div class="text-center">
            <button type="submit" class="bg-blue-500 text-white font-semibold rounded-lg py-2 px-4 hover:bg-blue-600 focus:outline-none focus:ring focus:border-blue-300">Submit</button>
        </div>
    </form>

</div>
